// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

contract VRF is Ownable, ReentrancyGuard {
    address public oracle;
    uint256 private randNumber;
    // Track pending requests
    mapping(bytes32 => bool) public pendingRequests;

    // Mapping from requestId to the resulting randomness
    mapping(bytes32 => uint256[]) public randomWords;

    // Events
    event RandomWordsRequested(
        bytes32 indexed requestId,
        address indexed requester
    );
    event RandomWordsFulfilled(
        bytes32 indexed requestId,
        uint256[] randomWords
    );
    event UpdateOracle(address indexed oldOracle, address indexed newOracle);

    // Errors
    error Unauthorized();
    error RequestNotFound();
    error InvalidSignature();

    modifier onlyOracle() {
        if (msg.sender != oracle) revert Unauthorized();
        _;
    }

    constructor(address _oracle) {
        require(_oracle != address(0), "Oracle address cannot be zero");
        oracle = _oracle;
        randNumber = uint256(
            keccak256(abi.encodePacked(msg.sender, block.timestamp, randNumber))
        );
        emit UpdateOracle(address(0), _oracle);
    }

    /**
     * Change the trusted oracle address
     */
    function setTrustedOracle(address _newOracle) external onlyOwner {
        require(_newOracle != address(0), "New oracle address cannot be zero");
        oracle = _newOracle;
        emit UpdateOracle(oracle, _newOracle);
    }

    /**
     * Request a new random number
     * Returns a unique requestId that can be used to retrieve the result
     */
    function requestRandomWords() external returns (bytes32 requestId) {
        // Create a unique request ID
        randNumber = uint256(
            keccak256(abi.encodePacked(msg.sender, block.timestamp, randNumber))
        );
        requestId = keccak256(
            abi.encodePacked(
                block.timestamp,
                block.prevrandao,
                msg.sender,
                address(this),
                randNumber
            )
        );

        // Mark request as pending
        pendingRequests[requestId] = true;

        // Emit event for off-chain oracle to detect
        emit RandomWordsRequested(requestId, msg.sender);

        return requestId;
    }

    /**
     * Oracle callback function to deliver randomness with cryptographic proof
     */
    function fulfillRandomWords(
        bytes32 requestId,
        uint256[] memory _randomWords,
        bytes calldata signature
    ) external onlyOracle nonReentrant {
        // Check if this is a valid request
        if (!pendingRequests[requestId]) revert RequestNotFound();

        // Verify the signature
        if (!verifySignature(requestId, _randomWords, signature))
            revert InvalidSignature();

        // Store the random result
        randomWords[requestId] = _randomWords;

        // Clean up
        delete pendingRequests[requestId];

        // Emit an event
        emit RandomWordsFulfilled(requestId, _randomWords);
    }

    /**
     * Verify that the signature is valid and from our trusted oracle
     */
    function verifySignature(
        bytes32 requestId,
        uint256[] memory _randomWords,
        bytes calldata signature
    ) internal view returns (bool) {
        // Create message hash from requestId and randomness
        bytes32 messageHash = keccak256(
            abi.encodePacked(requestId, _randomWords)
        );

        // Convert to Ethereum signed message hash
        bytes32 ethSignedMessageHash = keccak256(
            abi.encodePacked("\x19Ethereum Signed Message:\n32", messageHash)
        );

        // Recover the signer from the signature
        address recoveredSigner = recoverSigner(
            ethSignedMessageHash,
            signature
        );

        // Verify signer is our trusted oracle
        return recoveredSigner == oracle;
    }

    /**
     * Recover the signer's address from a signature
     */
    function recoverSigner(
        bytes32 ethSignedMessageHash,
        bytes calldata signature
    ) internal pure returns (address) {
        require(signature.length == 65, "Invalid signature length");

        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            // First 32 bytes stores the length (not needed)
            r := calldataload(signature.offset)
            s := calldataload(add(signature.offset, 32))
            v := byte(0, calldataload(add(signature.offset, 64)))
        }

        // EIP-2 still allows signature malleability for ecrecover(). Remove this possibility
        if (
            uint256(s) >
            0x7FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF5D576E7357A4501DDFE92F46681B20A0
        ) {
            revert InvalidSignature();
        }

        // Valid v values are 27 or 28
        if (v != 27 && v != 28) {
            revert InvalidSignature();
        }

        // Recover the signer
        return ecrecover(ethSignedMessageHash, v, r, s);
    }
}

